package inmakes_program;

public class Program3_ArithmeticOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 5;
		System.out.println("Addition is "+(a + b));
		System.out.println("Substraction is "+(a - b));
		System.out.println("Multiplication values is "+(a * b));
		System.out.println("Division values "+(a / b));
		System.out.println("WHat is this "+(a % b));
	}

}
