package inmakes_program;

public class Program4_LeftShiftoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Leftshift Opertor values is "+(10 << 2)); // 10*2Power(2) = 10*4 =40
		System.out.println("Leftshift Opertor values is "+(10 << 4)); // 10*2power(4) = 10*16=160
		System.out.println("Leftshift Opertor values is "+(20 << 5)); // 20*2power(5) = 20*32=640
	}

}
